<?php $link='&nbsp;<a class="link redirection" href="services">'.str_repeat('<span class="content">this page</span>',2).'</a>';$next='&nbsp;<a class="link redirection" href="services">'.str_repeat('<span class="content">next</span>',2).'</a>';$button='<a class="button redirection" href="contacts">'.str_repeat('<span class="content">contact</span>',2).'</a>';function createMenuItem($name){echo '<a class="menu-item'.(strpos($_SERVER["REQUEST_URI"],$name)?' current-item"':'"')."href=\"$name\">".str_repeat("<span class=\"content\">$name</span>",2).'</a>';}function createListLink($arrayLinks){$html;for($i=0;$i<count($arrayLinks);$i++)$html.="<li><a class=\"link\" href=\"{$arrayLinks[$i][1]}\" target=\"_blank\"><span>".str_repeat("<span class=\"content\">{$arrayLinks[$i][0]}:</span>",2).'</span>&nbsp;'.str_replace(['mailto:','http://'],'',$arrayLinks[$i][1]).'</a></li>';echo $html;}function createCheckbox($arrayLabels){$html;for($i=0;$i<count($arrayLabels);$i++)$html.='<label class="checkbox-area"><input'.($arrayLabels[$i][1]?' class="required"':" name=\"checkbox_$i\"").'type="checkbox"><span class="checkbox"><svg xmlns="http://www.w3.org/2000/svg" width="26" height="26" viewBox="0 0 26 26"><path class="line" d="M5.5,5.5l15,15"/><path class="line" d="M5.5,20.5l15-15"/></svg>'."</span><span>{$arrayLabels[$i][0]}</span></label>";echo $html;}function createImg($projectFolder,$video=false){static $count=0;$regexp=substr_count($projectFolder,"_")>1?"/(?:^|_)([a-z])/":"/(?:^|_)([a-z]{2})/";preg_match_all($regexp,$projectFolder,$matches);$fileName=implode($matches[1]);$link="../../images/projects/$projectFolder/$fileName-$count.webp";$imgInfo=getimagesize($link);$ratio=$imgInfo[1]/$imgInfo[0];if($video)echo '<video loop="true" muted="true" poster="'."$link\" data-ratio=\"$ratio\"><source src=\"".substr_replace($link,'m',-1).'" type="video/webm"></video>';else echo"<img src=\"$link\" alt=\"\" data-ratio=\"$ratio\">";$count++;}